package ejercicios;

public class Ejercicio1App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valorA = 10;
		int valorB = 15;
		int suma = valorA + valorB;
		int resta = valorA - valorB;
		int multi = valorA * valorB;
		int division = valorA / valorB;
		int modul = valorA % valorB;
		System.out.println("Resultado suma: " + suma);
		System.out.println("Resultado resta: " + resta);
		System.out.println("Resultado multiplicación: " + multi);
		System.out.println("Resultado división: " + division);
		System.out.println("Resultado módulo: " + modul);
	}

}
