package ejercicios;

public class Ejercicio2App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		double a = 10.65;
		char c = 'A';
		int numero = c;
		double suma = n + a;
		double resta = n - a;
		System.out.println("Valor de n: " + n);
		System.out.println("Valor de a: " + a);
		System.out.println("Valor de c: " + c);
		System.out.println("Suma de "+ n + " + " + a + " = " + suma);
		System.out.println("Resta de "+ n + " - " + a + " = " + resta);
		System.out.println("Valor númerico del carácter: " + numero);
	}

}
