package ejercicios;

public class Ejercicio4App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 10;
		int incremento = n + 77;
		int decremento = n - 3;
		System.out.println("Incremento de " + n + ", 77 posiciones: " + incremento);
		System.out.println("Decremento de " + n + ", 3 posiciones: " + decremento);
		System.out.println("Valor duplicado de " + n + ": " + n*2);
	}
	
}
